import { createStore, combineReducers } from 'redux';
import videoReducers from './reducers/videoReducers.js';
let reducer = combineReducers({
    video: videoReducers
})
let store = createStore(reducer);

/*
store.dispatch({
    type: "FETCH_VIDEOS",
    videoType: "trending"
});
*/
export {store};
